--------------- SQL ---------------CREATE OR REPLACE FUNCTION alm.ft_item_ime (  p_administrador integer,  p_id_usuario integer,  p_tabla varchar,  p_transaccion varchar)RETURNS varchar AS$body$/************************************************************************** SISTEMA:        Almacenes FUNCION:        alm.ft_item_ime DESCRIPCION:    Funcion que gestiona las operaciones basicas (inserciones, modificaciones, eliminaciones) de la tabla 'alm.titem' AUTOR:          Gonzalo Sarmiento FECHA:          21-09-2012 COMENTARIOS:   *************************************************************************** HISTORIAL DE MODIFICACIONES: DESCRIPCION:    AUTOR:            FECHA:       ***************************************************************************/DECLARE    v_nro_requerimiento        integer;    v_parametros               record;    v_id_requerimiento         integer;    v_resp                    varchar;    v_nombre_funcion        text;    v_mensaje_error         text;    v_id_item    integer;    v_num_por_clasificacion		integer;    v_codigo_acumulado			varchar;               BEGIN    v_nombre_funcion = 'alm.ft_item_ime';    v_parametros = pxp.f_get_record(p_tabla);    /*********************************        #TRANSACCION:  'SAL_ITEM_INS'     #DESCRIPCION:  Insercion de registros     #AUTOR:        Gonzalo Sarmiento        #FECHA:        21-09-2012    ***********************************/    if(p_transaccion='SAL_ITEM_INS')then                           begin        	IF v_parametros.id_clasificacion is not null THEN            	UPDATE alm.tclasificacion SET                	estado = 'restringido'                WHERE id_clasificacion = v_parametros.id_clasificacion;        	ELSE            	v_codigo_acumulado = 'Sin clasificar';            END IF;        	--Sentencia de la insercion            insert into alm.titem(            	id_usuario_reg,                fecha_reg,                estado_reg,                id_clasificacion,                codigo,                nombre,                descripcion,                palabras_clave,                codigo_fabrica,                observaciones,                numero_serie,                id_unidad_medida,                precio_ref,                id_moneda            ) values (                p_id_usuario,                now(),                'activo',                v_parametros.id_clasificacion,                v_codigo_acumulado,                v_parametros.nombre,                v_parametros.descripcion,                v_parametros.palabras_clave,                v_parametros.codigo_fabrica,                v_parametros.observaciones,                v_parametros.numero_serie,                v_parametros.id_unidad_medida,                v_parametros.precio_ref,                param.f_get_moneda_base()            ) RETURNING id_item into v_id_item;                          --Definicion de la respuesta            v_resp = pxp.f_agrega_clave(v_resp,'mensaje','Item almacenado(a) con exito (id_item'||v_id_item||')');            v_resp = pxp.f_agrega_clave(v_resp,'id_item',v_id_item::varchar);            --Devuelve la respuesta            return v_resp;        end;    /*********************************        #TRANSACCION:  'SAL_ITEM_MOD'     #DESCRIPCION:  Modificacion de registros     #AUTOR:        Gonzalo Sarmiento        #FECHA:        21-09-2012    ***********************************/    elsif(p_transaccion='SAL_ITEM_MOD')then        begin        	IF v_parametros.id_clasificacion is not null THEN            	UPDATE alm.tclasificacion SET                	estado = 'restringido'                WHERE id_clasificacion = v_parametros.id_clasificacion;            END IF;            --Sentencia de la modificacion            update alm.titem set            	id_usuario_mod = p_id_usuario,                fecha_mod = now(),                id_clasificacion = v_parametros.id_clasificacion,                nombre = v_parametros.nombre,                descripcion = v_parametros.descripcion,                palabras_clave = v_parametros.palabras_clave,                codigo_fabrica = v_parametros.codigo_fabrica,                observaciones = v_parametros.observaciones,                            numero_serie = v_parametros.numero_serie,                id_unidad_medida = v_parametros.id_unidad_medida,                precio_ref = v_parametros.precio_ref            where id_item=v_parametros.id_item;                          --Definicion de la respuesta            v_resp = pxp.f_agrega_clave(v_resp,'mensaje','Item modificado(a)');            v_resp = pxp.f_agrega_clave(v_resp,'id_item',v_parametros.id_item::VARCHAR);                          --Devuelve la respuesta            return v_resp;        end;    /*********************************        #TRANSACCION:  'SAL_ITEM_ELI'     #DESCRIPCION:  Eliminacion de registros     #AUTOR:        Gonzalo Sarmiento        #FECHA:        21-09-2012    ***********************************/    elsif(p_transaccion='SAL_ITEM_ELI')then        begin            update alm.titem set            	estado_reg = 'inactivo'            where id_item = v_parametros.id_item;                          --Definicion de la respuesta            v_resp = pxp.f_agrega_clave(v_resp,'mensaje','Item eliminado(a)');            v_resp = pxp.f_agrega_clave(v_resp,'id_item',v_parametros.id_item::varchar);                         --Devuelve la respuesta            return v_resp;        end;            /*********************************        #TRANSACCION:  'SAL_GENCODE_MOD'     #DESCRIPCION:  Modificacion del codigo de un item     #AUTOR:        Ariel Ayaviri     #FECHA:        05-02-2013    ***********************************/    elsif(p_transaccion='SAL_GENCODE_MOD')then        begin        	select max(num_por_clasificacion) into v_num_por_clasificacion            from alm.titem            where id_clasificacion = v_parametros.id_clasificacion;                        IF(v_num_por_clasificacion is null) THEN            	v_num_por_clasificacion := 1;            ELSE            	v_num_por_clasificacion := v_num_por_clasificacion + 1;            END IF;                        --se debe obtener el codigo de todos los padres del item seleccionado                        v_codigo_acumulado = alm.f_codigo_clasificaciones_recursivo(v_parametros.id_clasificacion);                        update alm.titem set            	codigo = v_codigo_acumulado||'.'||v_num_por_clasificacion,                num_por_clasificacion = v_num_por_clasificacion            where id_item = v_parametros.id_item;                        --Definicion de la respuesta            v_resp = pxp.f_agrega_clave(v_resp,'mensaje','Item eliminado(a)');            v_resp = pxp.f_agrega_clave(v_resp,'id_item',v_parametros.id_item::varchar);                        --Devuelve la respuesta            return v_resp;        end;            else        raise exception 'Transaccion inexistente: %',p_transaccion;    end if;EXCEPTION                   WHEN OTHERS THEN        v_resp='';        v_resp = pxp.f_agrega_clave(v_resp,'mensaje',SQLERRM);        v_resp = pxp.f_agrega_clave(v_resp,'codigo_error',SQLSTATE);        v_resp = pxp.f_agrega_clave(v_resp,'procedimientos',v_nombre_funcion);        raise exception '%',v_resp;                       END;$body$LANGUAGE 'plpgsql'VOLATILECALLED ON NULL INPUTSECURITY INVOKERCOST 100;